import { FortmaticConnector as FortmaticConnectorCore } from "@web3-react/fortmatic-connector";
import Fortmatic from "fortmatic";
import { ChainId, RpcUrl } from "../types";
export const OVERLAY_READY = "OVERLAY_READY";

type FormaticSupportedChains = Extract<
  ChainId,
  ChainId.MAINNET | ChainId.TESTNET
>;

const MAINNET = {
  rpcUrl: RpcUrl.MAINNET,
  chainId: ChainId.MAINNET,
};

// const TESTNET = {
//   rpcUrl: RpcUrl.TESTNET,
//   chainId: ChainId.TESTNET,
// };

export class FortmaticConnector extends FortmaticConnectorCore {
  async activate() {
    if (!this.fortmatic) {
      const { apiKey } = this as any;
      this.fortmatic = new Fortmatic(apiKey, MAINNET);
    }

    const provider = this.fortmatic.getProvider();

    const pollForOverlayReady = new Promise((resolve) => {
      const interval = setInterval(() => {
        if (provider.overlayReady) {
          clearInterval(interval);
          this.emit(OVERLAY_READY);
          resolve();
        }
      }, 200);
    });

    const [account] = await Promise.all([
      provider.enable().then((accounts: string[]) => accounts[0]),
      pollForOverlayReady,
    ]);

    return {
      provider: this.fortmatic.getProvider(),
      chainId: (this as any).chainId,
      account,
    };
  }
}
