      //       currency={currency}
      //       isSelected={isSelected}
      //       onSelect={handleSelect}
      //       otherSelected={otherSelected}
      //     />
      //   )
      // }
    },
    [
      // chainId,
      // inactiveTokens,
      // onCurrencySelect,
      // otherCurrency,
      // selectedCurrency,
      // setImportToken,
      // showImportView,
      // breakIndex,
      // theme.text1,
    ]
  );

  const itemKey = useCallback(
    (index: number, data: any) => currencyKey(data[index]),
    []
  );

  return (
    <FixedSizeList
      height={height}
      ref={fixedListRef as any}
      width="100%"
      itemData={itemData}
      itemCount={itemData.length}
      itemSize={56}
      itemKey={itemKey}
    >
      {Row}
    </FixedSizeList>
  );
}
