import { JSBI, Token } from "@uniswap/sdk";
import React, {
  useCallback,
  useContext,
  useMemo,
  useState,
  useEffect,
} from "react";
import { ThemeContext } from "styled-components";
import { AutoColumn } from "../../components/Column";
import { AutoRow } from "../../components/Row";
import { SearchInput } from "../../components/SearchModal/styleds";
import { useAllTokenV1Exchanges } from "../../data/V1";
import { useActiveWeb3React } from "../../hooks";
import { useToken } from "../../hooks/Tokens";
import { useTokenBalancesWithLoadingIndicator } from "../../state/wallet/hooks";
import { BackArrow, TYPE } from "../../theme";
import { LightCard } from "../../components/Card";
import { BodyWrapper } from "../AppBody";
import { EmptyState } from "./EmptyState";
import V1PositionCard from "../../components/PositionCard/V1";
import QuestionHelper from "../../components/QuestionHelper";
import { Dots } from "../../components/swap/styleds";
import { useAddUserToken } from "../../state/user/hooks";
import { isTokenOnList } from "../../utils";
import { useCombinedActiveList } from "../../state/lists/hooks";

export default function MigrateV1() {
  const theme = useContext(ThemeContext);
  const { account, chainId } = useActiveWeb3React();

  const [tokenSearch, setTokenSearch] = useState<string>("");
  const handleTokenSearchChange = useCallback(
    (e) => setTokenSearch(e.target.value),
    [setTokenSearch]
  );

  // automatically add the search token
  const token = useToken(tokenSearch);
  const selectedTokenListTokens = useCombinedActiveList();
  const isOnSelectedList = isTokenOnList(
    selectedTokenListTokens,
    token ?? undefined
  );
